import React from 'react';
import ReactMarkdown from 'markdown-to-jsx';
import { Box, Typography, Link } from '@mui/material';

function MarkdownListItem(props) {
  return <Box component="li" sx={{ mt: 1, typography: 'body1' }} {...props} />;
}

function ImageRenderer(props) {
  const imageSrc = props.src;
  const altText = props.alt;
  console.log("image",imageUrl)
  return (
    <img style={{ display: 'none' }} src={imageSrc} alt={altText}/>
  );
}

function Markdown(props) {
  const { content, imageUrl } = props;



  const renderers = {
    h1: ({ children }) => <Typography gutterBottom variant="h4" component="h1">{children}</Typography>,
    h2: ({ children }) => <Typography gutterBottom variant="h6" component="h2">{children}</Typography>,
    h3: ({ children }) => <Typography gutterBottom variant="subtitle1">{children}</Typography>,
    h4: ({ children }) => <Typography gutterBottom variant="caption" paragraph>{children}</Typography>,
    p: ({ children }) => <Typography paragraph>{children}</Typography>,
    a: ({ href, children }) => <Link href={href} target="_blank" rel="noopener noreferrer">{children}</Link>,
    li: MarkdownListItem,
    //img: ({ src, alt }) => <img src={imageUrl} alt={alt} style={{ maxWidth: '100%' }} />,
    "image": () => <ImageRenderer src= {imageUrl} alt="ALT TEXT" />,
  };

  return <ReactMarkdown renderers={renderers}>{content}</ReactMarkdown>;
}

export default Markdown;
